class App extends Component {

  state = {
    contacts: [
      { 
        name: "Aboba",
        number: "+38 (098) 22 864 31",
        image: 1,
        gender: "women"
       },
       { 
        name: "Aboba 2",
        number: "+38 (098) 22 864 31",
        image: 82,
        gender: "men"
       },
       { 
        name: "Aboba 3",
        number: "+38 (098) 22 864 31",
        image: 5,
        gender: "women"
       }
      ]
  }

  render() {
    return (
      <Fragment>
        <NavItem></NavItem>
        <div className="container-fluid">
          <div className="row">
            <div className="col-2">
              <h3>Add new contact:</h3>
              <AddContact></AddContact>
            </div>
                <ContactList ></ContactList>
            <div className="col-10">
              <div className="row">

              </div>
            </div>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default App;
