import React, { Component, Fragment } from 'react';
import './contact-item.css';


class ContactItem extends Component {

    state = {
        name: this.props.name,
        number: this.props.number,
        image: this.props.image,
        gender: this.props.gender,
        star: this.props.star
    }

    setRandomImage = () => {
        var randomAvatar = Math.floor(Math.random() * Math.floor(99));
        this.setState({
            image: randomAvatar
        });
    }

    setStarImage = () => {

        var el = document.getElementById("star");

        if (this.state.star == false)
            this.setState({
                star: true
            });
        else if (this.state.star == true)
            this.setState({
                star: true
            });
    }

    render() {
        const { name, number, image, gender, star } = this.state;
        const image_URL = `https://randomuser.me/api/portraits/${gender}/${image}.jpg`;

        return (
            <Fragment>
                <div className="card col-4">
                    <img className="card-img-top" src={image_URL} alt="Card image cap" />
                    <div className="card-body">
                        <h5 className="card-title">{name}</h5>
                        <p className="card-text">{number}</p>
                    </div>
                    <div className="card-footer" id="starDiv">
                        <button onClick={this.setRandomImage.bind(this)} className="btn btn-info">Random image</button>
                        <i class="far fa-star" id="starEl"></i>
                    </div>
                </div>
            </Fragment>
        );
    }
}

export default ContactItem;
