import React, { Component, Fragment } from 'react';
import './App.css';
import ContactItem from './Components/contact-item/contact-item';
import NavItem from './Components/navbar/Nav';
import AddContact from './Components/AddContact/addContact';
import ContactList from './Components/contact-list/contact-list';

class App extends Component {
  state = {
    contacts: [
      {
        id: 1,
        name: "Aboba",
        number: "+38 (098) 22 864 31",
        image: 1,
        gender: "women",
        isFavorite: false
      },
      {
        id: 2,
        name: "Aboba 2",
        number: "+38 (098) 22 864 31",
        image: 82,
        gender: "men",
        isFavorite: false
      },
      {
        id: 3,
        name: "Aboba 3",
        number: "+38 (098) 22 864 31",
        image: 5,
        gender: "women",
        isFavorite: false
      },
      {
        id: 4,
        name: "Aboba 3",
        number: "+38 (098) 22 864 31",
        image: 5,
        gender: "women",
        isFavorite: false
      }
    ]
  }

  addContact = (newContact) => {
    var tmpContacts = [];

    if (this.state.contacts !== null) {
      tmpContacts = this.state.contacts.slice();
    }

    tmpContacts.push(newContact);

    this.setState({
      contacts: tmpContacts
    })
  }

  removeContact = (contact) => {
    var tmpContacts = [];

    if (this.state.contacts !== null) {
      tmpContacts = this.state.contacts.slice();
      tmpContacts.splice(tmpContacts.findIndex(x => x.id == contact.id), 1);
    }

    this.setState({
      contacts: tmpContacts
    })
  }

  render() {
    return (
      <Fragment>
        <NavItem></NavItem>
        <div className="container-fluid">
          <div className="row">
            <div className="col-2">
              <h3>Add new contact:</h3>
              <AddContact addContact={this.addContact}></AddContact>
            </div>
            <div className="col-10">
              <div className="row">
                <ContactList re contacts={this.state.contacts}></ContactList>
              </div>
            </div>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default App;
