import React, { Component, Fragment } from 'react';
import './contact-item.css';


class ContactItem extends Component {

    state = {
        name: "Aboba",
        number: "+38 (098) 22 864 31",
        image: 1
    }

    setRandomImage = () => {
var randomAvatar = Math.floor(Math.random() * Math.floor())
    }



    render() {
        const { name, number, image } = this.state;
        const image_URL = `https://randomuser.me/api/portraits/men/${image}.jpg`;
        return (
            <Fragment>
                <div className="card col-4">
                    <img className="card-img-top" src={image_URL} alt="Card image cap" />
                    <div className="card-body">
                        <h5 className="card-title">{name}</h5>
                        <p className="card-text">{number}</p>
                    </div>
                    <div className="card-footer">
                        <button onClick={ this.setRandomImage } className="btn btn-info">Random image</button>
                    </div>
                </div>
            </Fragment>
        );
    }
}

export default ContactItem;
